INSERT INTO `wp_wc_admin_notes` VALUES("1", "wc-admin-onboarding-email-marketing", "info", "en_US", "Tips, product updates, and inspiration", "We\'re here for you - get tips, product updates and inspiration straight to your email box", "{}", "unactioned", "woocommerce-admin", "2020-08-26 17:11:16", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("2", "wc-admin-marketing-intro", "info", "en_US", "Connect with your audience", "Grow your customer base and increase your sales with marketing tools built for WooCommerce.", "{}", "unactioned", "woocommerce-admin", "2020-08-26 17:11:16", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("3", "wc-admin-wc-helper-connection", "info", "en_US", "Connect to WooCommerce.com", "Connect to get important product notifications and updates.", "{}", "unactioned", "woocommerce-admin", "2020-08-26 17:11:17", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("4", "wc-admin-onboarding-profiler-reminder", "update", "en_US", "Welcome to WooCommerce! Set up your store and start selling", "We\'re here to help you going through the most important steps to get your store up and running.", "{}", "unactioned", "woocommerce-admin", "2020-08-26 17:11:29", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("5", "wc-admin-coupon-page-moved", "update", "en_US", "Coupon management has moved!", "Coupons can now be managed from Marketing &gt; Coupons. Click the button below to remove the legacy WooCommerce &gt; Coupons menu item.", "{}", "unactioned", "woocommerce-admin", "2020-08-27 13:54:27", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("6", "wc-admin-mobile-app", "info", "en_US", "Install Woo mobile app", "Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.", "{}", "unactioned", "woocommerce-admin", "2020-08-29 16:17:33", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("7", "wc-admin-learn-more-about-product-settings", "info", "en_US", "Learn more about Product Settings", "In this video you\'ll find information about configuring product settings, such as how they are displayed, editing inventory options and so on.", "{}", "unactioned", "woocommerce-admin", "2020-08-29 16:17:37", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("8", "wc-admin-usage-tracking-opt-in", "info", "en_US", "Help WooCommerce improve with usage tracking", "Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://monjeka/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.", "{}", "unactioned", "woocommerce-admin", "2020-09-02 17:11:19", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("9", "wc-admin-store-notice-giving-feedback-2", "info", "en_US", "Give feedback", "Now that youve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.", "{}", "unactioned", "woocommerce-admin", "2020-09-03 17:11:52", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("10", "wc-admin-insight-first-sale", "survey", "en_US", "Did you know?", "A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?", "{}", "unactioned", "woocommerce-admin", "2020-09-03 17:11:52", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("11", "wc-admin-home-screen-feedback", "info", "en_US", "Help us improve the WooCommerce Home screen", "We\'d love your input to shape the future of the WooCommerce Home screen together. Feel free to share any feedback, ideas or suggestions that you have.", "{}", "unactioned", "woocommerce-admin", "2020-09-07 17:12:10", NULL, "0", "plain", "", "0", "info");
